const addresses = {
    1: 2361,
    2: 2355,
    3: 2327,
    4: 2306
};

const message = { 
    "action": "write", 
    "address": undefined, 
    "datatype": 5, 
    "value": undefined 
};

const regex = /^(.*)\/(?<id>\d+)$/
msg.blinds_id = regex.exec(msg.topic).groups['id'];
message.address = addresses[msg.blinds_id];
msg.message = message;

return msg;

if(msg.payload.contact == true){
    msg.payload = 'closed';
}else{
    msg.payload = 'open';
}

return msg;
